mapper.select:
SELECT * FROM student_exam_question


        student_uid = #{studentUid}


        and exam_id = #{examId}


        and question_index = #{questionIndex}

mapper.update:
UPDATE student_exam_question


        answer = #{answer},


        score = #{score}


WHERE student_uid = #{studentUid} AND exam_id = #{examId} AND question_index = #{questionIndex}
